
public class Asterisks {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int row = 0; row < 8; row++) {
			if(row%2==0) {
				System.out.println("* * * * * * * * ");
			}
			else {
				System.out.println(" * * * * * * * * ");
			}
			
		}
	}

}
